const cartLink=document.querySelectorAll(".add-item-cart"),cartPopup=document.querySelector(".modal__cart"),cartClose=cartPopup.querySelector(".modal__close-cart");cartLink.forEach(o=>{o.onclick=(()=>{cartPopup.classList.add("modal-show")})}),cartClose.addEventListener("click",function(o){o.preventDefault(),cartPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&cartPopup.classList.contains("modal-show")&&(o.preventDefault(),cartPopup.classList.remove("modal-show"))});
